&lt;pre&gt;&lt;code&gt;from PIL import Image

def flip_image(image_path, output_path):
    # ÊâìÂºÄÂõæ‚Ωö
    with Image.open(image_path) as img:
        # ‚ΩîÂπ≥ÁøªËΩ¨Âõæ‚ΩöÔºàÈïúÂÉèÔºâ
        flipped_img = img.transpose(Image.FLIP_LEFT_RIGHT)

