def from_hex(s):
    b_str = &#39;&#39;.join(format(int(s[i:i+2], 16), &#39;08b&#39;) for i in
range(0, len(s), 2))
    return b_str.rstrip(&#39;0&#39;)
def from_bit(s):
    d_str = &#39;&#39;.join(chr(int(s[i:i+7], 2)) for i in range(0, le
n(s), 7))
    return d_str

for code in codes.split(&#39;\n&#39;):
